test__ws(A, B, true) :-
	det__ws(A, B), !.
test__ws(A, C, false) :-
	prb__r(A, B),
	prb__ws(A, B, C).

det__max(3317044064679887385961980).

det__ws(B, A) :-
	det__ws__tbl(C, A),
	B<C, !.

prb__r(B, A) :-
	(   prb__r__tbl(C, A),
	    B<C, !
	;   A=1
	).

prb__ws(A, B, E) :-
	C is A+ -2,
	findall(D,
		( between(1, B, _),
		  random_between(2, C, D)
		),
		E).

